var request = require("request");

var WotApi = {
    host: "https://api.worldoftanks.ru/wot",
    form: {
        application_id: "806c213f43a41761f2d80c8e65f5fa61"
    },
    clan: function(id, calback) {
        this.host += "/clan/info/";
        this.form.clan_id = id;
        request.post(this.host, {form: this.form}, function (error, response, body){
            if (!error && response.statusCode == 200) {
                calback(JSON.parse(body));
            } else {
                throw error;
            }
        });
    }
}
module.exports = WotApi;