var errorResponse = require("./response/error.js");
var httpClient = require("request");

var WotApi = {
    host: "",
    form: {},
    clan: function(id, calback) {
        this.host += "/clan/info/";
        this.form.clan_id = id;
        httpClient.post(this.host, {form: this.form}, function (error, response, body){
            if (!error && response.statusCode == 200) {
                calback(JSON.parse(body));
            } else {
                calback(errorResponse(error.message));
            }
        });
    }
}
module.exports = WotApi;